insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92150','Fatal error: web client version is too new. ','The version of the client is newer than the version of the Server.','The system administrator should ensure that the Forms product is correctly installed on the Forms server machine. ',' Java console, alert','99','None','N');
