insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92180','Fatal error: JavaScript is unable to obtain the server URL. This can occur if legacy_lifecycle=true and JavaScript has been disabled. If so, you will need to reenable JavaScript, restart the browser, and restart your application. ','The serverURL applet parameter specified a value of "?", which indicates that the serverURL should be obtained from an element outside of the applet. (In a page that''s generated from a standard base HTML file, this occurs when legacy_lifecycle=true in formsweb.cfg). Forms attempted to obtain the serverURL using JavaScript, but the attempt failed, probably because JavaScript has been disabled.','Reenable JavaScript and restart the browser. If that does not solve the problem, examine the stack trace that accompanies this message. If the stack trace indicates a possible cause, correct it. If the problem persists, contact Oracle Support Services. ',' Java console, alert','99','None','N');
insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92190','JavaScript is unable to evaluate expression. ','The Forms application executed the web.JavaScript_Eval_Expr built-in, but an invalid Javascript expression was specified as the first argument.','Correct the Javascript expression that is passed to the web.JavaScript_Eval_Expr built-in. ',' Java console, alert','99','None','N');
