insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92210','invalid value %s for lookAndFeel applet parameter. Defaulting to %s. ','An invalid value was specified for the lookAndFeel applet parameter.','The system administrator should ensure that a valid value was specified for the lookAndFeel applet parameter. ',' Java console, applet status window','99','None','N');
