insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92220','access to system clipboard denied ','The system clipboard is locked by some other application. This generally indicates a problem with the other application. Note: If the allowAlertClipboard applet parameter is set to ''false'', this message appears only on the Java console.','Determine which application is locking the system clipboard, and terminate it (or terminate the action that''s locking the clipboard). Then, if possible, correct the application so that it does not erroneously lock the system clipboard. ',' Java console, alert','99','None','N');
insert into FRM_ERROR (CODE,ERRO,CAUSE,ACTION,APPEARS,NIVEL,GATILHO,FAVORITE) values ('FRM-92410','EndUserMonitoring initialization has failed. Verify that %s (specified by the applet parameter %s) is a valid URL. ','EndUserMonitoring initialization failed, probably due to an invalid URL specified by the specified applet parameter (typically the EndUserMonitoringURL parameter). The applet parameter was ignored; EndUserMonitoring was then disabled and execution continued.','The system administrator should ensure that a valid URL was specified for the specified applet parameter. If that does not solve the problem, examine the stack trace that accompanies this message. If the stack trace indicates a possible cause, correct it. If the problem persists, contact Oracle Support Services. ',' Java console','99','None','N');
